import React from 'react';
import './HomePage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFacebook, faTwitter } from '@fortawesome/free-brands-svg-icons';
import enTranslations from './locales/en.json';
import arTranslations from './locales/ar.json';
import { LanguageContext } from './LanguageContext';
function HomePage() {
  const { language, setLanguage } = useContext(LanguageContext); 
  const translations = language === 'en' ? enTranslations : arTranslations;

          const handleLearnMoreClick = () => {
          window.location.href = '/about';//learn more
          };

  return (
    <div>
      <div className="image">
        <img src='/images/School.jpg' alt='image1' />
      </div>

      
      <section className="info">
        <div className="info-content">
          <h2>{translations.welcome}</h2>
          <p>Providing Quality Education Since 2000</p>
          
          <button onClick={handleLearnMoreClick}>Learn More</button>
        
        </div>
      </section>

    
      <section className="events-news">
        <div>
          <h3>School Events and News</h3>
        </div>
        <div >
          <ul>
            <li>Next event: Holiday in the Hijri New Year on 7/20/2023</li>
            <li>News: Students win a math competition</li>
          
          </ul>
        </div>
      </section>

    
      <section className="social">
        <div>
          <h3>Social Media Integration</h3>
        </div>
        <div className="section-content">
          <a href="https://www.facebook.com">
            <FontAwesomeIcon icon={faFacebook} size="2x" />
          </a>
          <a href="https://www.twitter.com">
            <FontAwesomeIcon icon={faTwitter} size="2x" />
          </a>
          
        </div>
      </section>
    </div>
  );
}

export default HomePage;
