import React, { useState } from 'react';
import './style/HomePage.css';
const imageUrls = [
  '\images\School.jpg',
  '/images/school1.jpg',
  '/images/school3.jpg',
  '/images/school2.jpeg'
  
]

function HomePage() {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const showPreviousImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === 0 ? imageUrls.length - 1 : prevIndex - 1));
  };

  const showNextImage = () => {
    setCurrentImageIndex((prevIndex) => (prevIndex === imageUrls.length - 1 ? 0 : prevIndex + 1));
  };

  return (
    <div>
      <div className="image-container">

        <img src={imageUrls[currentImageIndex]} alt={`Image ${currentImageIndex + 1}`} style={{ maxWidth: '100%' }} />
      </div>
      <div style={{ textAlign: 'center', marginTop: '1rem' }}>
        <button onClick={showPreviousImage}>Previous</button>
        <button onClick={showNextImage}>Next</button>
      </div>
    </div>
  );
}

export default HomePage;
